struct stat /* structure returned by stat (size = 0x24) */{    int   st_dev ;    /* 00 - device number             */    int   st_ino ;    /* 02 - fdn number                */    unsigned st_mode; /* 04 - file mode and permissions */    char st_nlink;    /* 06 - file link count           */    int st_uid;       /* 07 - file owner's user id      */    /* int st_gid;    /* 09 - file owner's group id     */    long st_size;     /* 09 - file size in bytes        */    long st_mtime;    /* 0D - last modified time        */    /* long st_atime; /* 17 - last time accessed        */    /* long st_ctime; /* 17 - last originally created   */    long st_spr;      /* 17 - spare - future use only   */};